/* jshint unused:false */
'use strict';

var Replicant = require('./browser/replicant');
var io = {};
var server = require('./server');
var filteredConfig = require('./config').getFilteredConfig();
var utils = require('./util');
var replicator = require('./replicator');

/**
 * Creates a new NodeCG extension API instance.
 * @constructor
 * @param {object} bundle The bundle object to build an API instance from.
 */
function NodeCG(bundle, socket) {
    var self = this;

    // Make bundle name and config publicly accessible
    this.bundleName = bundle.name;
    this.bundleConfig = bundle.config;

    // Make logger publicly accessible
    this.log = require('./browser/logger')(bundle.name);

    this._messageHandlers = [];

    if (true) {
        // This constructor only works if socket.io is loaded
        if (typeof io === 'undefined') {
            throw new Error('[nodeceg] Socket.IO must be loaded before instantiating the API');
        }

        // If title isn't set, set it to the bundle name
        document.addEventListener('DOMContentLoaded', function() {
            if (document.title === '') document.title = self.bundleName;
        }, false);

        // Make socket accessible to public methods
        this.socket = socket;
        this.socket.emit('joinRoom', bundle.name);

        // Upon receiving a message, execute any handlers for it
        socket.on('message', function onMessage(data) {
            self.log.trace('Received message %s (sent to bundle %s) with data:',
                self.bundleName, data.messageName, data.bundleName, data.content);

            self._messageHandlers.forEach(function(handler) {
                if (data.messageName === handler.messageName &&
                    data.bundleName === handler.bundleName) {
                    handler.func(data.content);
                }
            });
        });

        socket.on('error', function(err) {
            if (err.type === 'UnauthorizedError') {
                var url = [location.protocol, '//', location.host, location.pathname].join('');
                window.location.href = '/authError?code='+err.code+'&message='+err.message+'&viewUrl='+url;
            } else {
                self.log.error('Unhandled socket error:', err);
            }
        });
    } else {
        io = server.getIO();

        io.on('connection', function onConnection(socket) {
            socket.setMaxListeners(64); // Prevent console warnings when many extensions are installed
            socket.on('message', function onMessage(data, cb) {
                self.log.trace('[%s] Received message %s (sent to bundle %s) with data:',
                    self.bundleName, data.messageName, data.bundleName, data.content);

                self._messageHandlers.forEach(function(handler) {
                    if (data.messageName === handler.messageName &&
                        data.bundleName === handler.bundleName) {
                        handler.func(data.content, cb);
                    }
                });
            });
        });
    }

    // Create read-only config property, which contains the current filtered NodeCG config
    Object.defineProperty(this, 'config', {
        value: filteredConfig,
        writable: false,
        enumerable: true
    });
    Object.freeze(this.config);
}

// ###NodeCG prototype

/**
 * Sends a message within the current bundle.
 * @param {string}   messageName The name of the message.
 * @param {mixed}    [data]      The data to send.
 */
NodeCG.prototype.sendMessage = function (messageName, data, callback) {
    if (typeof callback === 'undefined' && typeof data === 'function') {
        callback = data;
        data = null;
    }

    this.sendMessageToBundle(messageName, this.bundleName, data, callback);
};

/**
 * Sends a message to a specific bundle.
 * @param {string}   messageName The name of the message.
 * @param {string}   bundleName  The name of the target bundle.
 * @param {mixed}    [data]      The data to send
 */
NodeCG.sendMessageToBundle = function (messageName, bundleName, data, callback) {
    if (true) {
        if (typeof callback === 'undefined' && typeof data === 'function') {
            callback = data;
            data = null;
        }

        window.socket.emit('message', {
            bundleName: bundleName,
            messageName: messageName,
            content: data
        }, callback);
    } else {
        io.emit('message', {
            bundleName: bundleName,
            messageName: messageName,
            content: data
        });
    }
};

NodeCG.prototype.sendMessageToBundle = function (messageName, bundleName, data, callback) {
    this.log.trace('[%s] Sending message %s to bundle %s with data:',
        this.bundleName, messageName, bundleName, data);

    NodeCG.sendMessageToBundle.apply(NodeCG, arguments);
};

/**
 * Listens for a message.
 * @param {string}   messageName                     The name of the message.
 * @param {string}   [bundleName=CURRENT_BUNDLE_NAME] The bundle namespace to in which to listen for this message
 * @param {function} handler                          The callback fired when this message is received.
 */
NodeCG.prototype.listenFor = function (messageName, bundleName, handler) {
    if (typeof handler === 'undefined') {
        handler = bundleName;
        bundleName = this.bundleName;
    }

    this.log.trace('[%s] Listening for %s from bundle %s', this.bundleName, messageName, bundleName);

    // Check if a handler already exists for this message
    var len = this._messageHandlers.length;
    for (var i = 0; i < len; i++) {
        var existingHandler = this._messageHandlers[i];
        if (messageName === existingHandler.messageName && bundleName === existingHandler.bundleName) {
            this.log.error('%s attempted to declare a duplicate "listenFor" handler:',
                this.bundleName, bundleName, messageName);
            return;
        }
    }

    this._messageHandlers.push({
        messageName: messageName,
        bundleName: bundleName,
        func: handler
    });
};

NodeCG.Replicant = function(name, bundle, opts) {
    return new Replicant(name, bundle, opts, true ? window.socket : null);
};

NodeCG.prototype.Replicant = function(name, bundle, opts) {
    if (!bundle || typeof bundle !== 'string') {
        opts = bundle;
        bundle = this.bundleName;
    }

    return NodeCG.Replicant(name, bundle, opts);
};

/**
 * Reads the value of a synchronized variable without creating a subscription to it.
 * @param {string}   name                         The name of the variable.
 * @param {string}   [bundle=CURRENT_BUNDLE_NAME] The bundle namespace to in which to look for this replicant
 */
NodeCG.readReplicant = function(name, bundle, cb) {
    if (!name || typeof name !== 'string') {
        throw new Error('Must supply a name when reading a Replicant');
    }

    if (!bundle || typeof bundle !== 'string') {
        throw new Error('Must supply a bundle name when reading a Replicant');
    }

    if (true) {
        window.socket.emit('readReplicant', {name: name, bundle: bundle}, cb);
    } else {
        var replicant = replicator.find(name, bundle);
        if (replicant) return replicant.value;
    }
};

NodeCG.prototype.readReplicant = function(name, bundle, cb) {
    if (!bundle || typeof bundle !== 'string') {
        cb = bundle;
        bundle = this.bundleName;
    }

    return NodeCG.readReplicant(name, bundle, cb);
};

if (true) {
    window.NodeCG = NodeCG;

    /**
     * Returns the nearest element with the desired attribute.
     * Begins at startEl and then checks every parent until one of them has the attribute.
     * @param   {object} startEl   The element on which to begin the search.
     * @param   {string} attrName  The attribute name to look for.
     * @returns {object}
     */
    NodeCG.nearestElementWithAttribute = function(startEl, attrName) {
        var target = startEl;
        while (target) {
            if (target.hasAttribute) {
                if (target.hasAttribute(attrName)) {
                    return target;
                }
            }
            target = target.parentNode;
        }
    };
} else {
    /**
     * Gets the server Socket.IO context.
     */
    NodeCG.prototype.getSocketIOServer = server.getIO;

    /**
     * Mounts express middleware to the main server express app.
     * See http://expressjs.com/api.html#app.use for usage.
     */
    NodeCG.prototype.mount = server.mount;

    NodeCG.prototype.util = {};

    /**
     * Checks if a session is authorized. Intended to be used in express routes.
     * @param {object}   req   A HTTP request.
     * @param {object}   res   A HTTP response.
     * @param {function} next  The next middleware in the control flow.
     */
    NodeCG.prototype.util.authCheck = utils.authCheck;

    /**
     * Finds and returns a session that matches the given parameters.
     * @param {object} params   A NeDB search query [https://github.com/louischatriot/nedb#finding-documents].
     * @returns {object}
     */
    NodeCG.prototype.util.findSession = utils.findSession;

    // Make all extensions accessible via 'nodecg.extensions'
    Object.defineProperty(NodeCG.prototype, 'extensions', {
        get: function() {
            return server.getExtensions();
        },
        enumerable: true
    });
}

module.exports = NodeCG;
'use strict';

var config = require('../config').getFilteredConfig();
var logLevel = config.logging.console.level;
var logReplicants = config.logging.replicants;

var LOG_LEVELS = {
    trace: 0,
    debug: 1,
    info: 2,
    warn: 3,
    error: 4
};

module.exports = function(filename) {
    var loggerInstance = {};

    loggerInstance.trace = function() {
        if (LOG_LEVELS[logLevel] > LOG_LEVELS.trace) return;
        arguments[0] = '['+filename+'] ' + arguments[0];
        console.info.apply(console, arguments);
    };

    loggerInstance.debug = function() {
        if (LOG_LEVELS[logLevel] > LOG_LEVELS.debug) return;
        arguments[0] = '['+filename+'] ' + arguments[0];
        console.info.apply(console, arguments);
    };

    loggerInstance.info = function() {
        if (LOG_LEVELS[logLevel] > LOG_LEVELS.info) return;
        arguments[0] = '['+filename+'] ' + arguments[0];
        console.info.apply(console, arguments);
    };

    loggerInstance.warn = function() {
        if (LOG_LEVELS[logLevel] > LOG_LEVELS.warn) return;
        arguments[0] = '['+filename+'] ' + arguments[0];
        console.log.apply(console, arguments);
    };

    loggerInstance.error = function() {
        if (LOG_LEVELS[logLevel] > LOG_LEVELS.error) return;
        arguments[0] = '['+filename+'] ' + arguments[0];
        console.error.apply(console, arguments);
    };

    loggerInstance.replicants = function() {
        if (!logReplicants) return;
        arguments[0] = '['+filename+'] ' + arguments[0];
        console.info.apply(console, arguments);
    };

    return loggerInstance;
};
'use strict';

var inherits = require('inherits'); // Used instead of full `util` package for the sake of easier browserification
var EventEmitter = require('events').EventEmitter;
var Nested = require('nested-observe');
var objectPath = require('object-path');
var equal = require('deep-equal');
var clone = require('clone');
var uuid = require('uuid');
var declaredReplicants = {};

// Make Replicant inherit the prototype of EventEmitter
inherits(Replicant, EventEmitter);
module.exports = Replicant;

function Replicant(name, bundle, opts, socket) {
    if (!name || typeof name !== 'string') {
        throw new Error('Must supply a name when instantiating a Replicant');
    }

    if (!bundle || typeof bundle !== 'string') {
        throw new Error('Must supply a bundle name when instantiating a Replicant');
    }

    // Load logger
    var log = require('./logger')('Replicant/' + bundle + '.' + name);

    // If replicant already exists, return that.
    if (declaredReplicants.hasOwnProperty(bundle)) {
        if (declaredReplicants[bundle].hasOwnProperty(name)) {
            return declaredReplicants[bundle][name];
        } else {
            declaredReplicants[bundle][name] = this;
        }
    } else {
        declaredReplicants[bundle] = {};
        declaredReplicants[bundle][name] = this;
    }

    var self = this;
    var value;
    opts = opts || {};
    this.name = name;
    this.bundle = bundle;
    this.opts = opts;
    this.revision = 0;
    this.status = 'undeclared';
    this.id = uuid.v4();
    if (typeof opts.persistent === 'undefined') opts.persistent = true;

    // When running in the browser, we have to wait until the socket joins the room
    // and the replicant is fully declared before running any additional commands.
    // After this time, commands do not need to be added to the queue and are simply executed immediately.
    var queue = [];

    function addToQueue(fn, args) {
        queue.push({fn: fn, args: args});
    }

    function processQueue() {
        queue.forEach(function(item) {
            item.fn.apply(this, item.args);
        });
    }

    assignValue(opts.defaultValue);

    Object.defineProperty(this, 'value', {
        get: function() {
            return value;
        },
        set: function(newValue) {
            if (newValue === value) {
                log.replicants('value unchanged, no action will be taken');
                return value;
            }

            log.replicants('running setter with', newValue);

            if (self.status !== 'declared') {
                addToQueue(doBrowserSetter, [newValue]);
                return;
            }

            doBrowserSetter(newValue);
        },
        enumerable: true
    });

    function doBrowserSetter(newValue) {
        socket.emit('assignReplicant', {
            name: name,
            bundle: bundle,
            value: newValue,
            originatorId: self.id
        });
    }

    function doBrowserDeclare(defaultValue) {
        if (self.status === 'declared' || self.status === 'declaring') return;
        self.status = 'declaring';
        socket.emit('joinRoom', bundle, function() {
            socket.emit('declareReplicant', {
                name: name,
                bundle: bundle,
                defaultValue: defaultValue,
                persistent: opts.persistent
            }, function(data) {
                log.replicants('declareReplicant callback (value: %s, revision: %s)', data.value, data.revision);
                var didMismatchReassignment = false;
                if (self.revision !== data.revision || !equal(value, data.value)) {
                    unobserveValue();
                    value = defaultValue;
                    observeValue();
                    assignValue(data.value, data.revision);
                    didMismatchReassignment = true;
                }
                self.status = 'declared';
                self.emit('declared', data);
                if (queue.length > 0) {
                    processQueue();
                } else if (!didMismatchReassignment) {
                    self.emit('change', undefined, data.value);
                }
            });
        });
    }

    doBrowserDeclare(opts.defaultValue);

    // Overwrites the value completely, and assigns a new one.
    // Does not send a `change` argument with the change event.
    function assignValue(newValue, revision) {
        unobserveValue();
        var oldValue = clone(value);
        value = newValue;
        observeValue();
        if (typeof revision !== 'undefined') {
            self.revision = revision;
        }
        self.emit('change', oldValue, value);
    }

    function observeValue() {
        if (typeof value === 'object' && value !== null) {
            Nested.observe(value, onValueChange);
        }
    }

    function dispatchChanges(changes) {
        socket.emit('changeReplicant', {
            name: name,
            bundle: bundle,
            changes: changes,
            revision: self.revision,
            originatorId: self.id
        }, function(value, revision) {
            // This callback is only invoked in cases of failure
            log.replicants('Not at head revision (ours %s, theirs %s). Change aborted & head revision applied.',
                self.revision, revision);
            assignValue(value, revision);
        });
    }

    function onValueChange(rawChanges) {
        var formattedChanges = [];
        rawChanges.forEach(function(change) {
            var path = change.path.substr(1).replace(/\//g,'.');
            var newVal = objectPath.get(change.root, path);
            switch(change.type) {
                case 'add':
                    formattedChanges.push({
                        type: 'add',
                        path: path,
                        newValue: newVal
                    });
                    break;
                case 'update':
                    formattedChanges.push({
                        type: 'update',
                        path: path,
                        oldValue: change.oldValue,
                        newValue: newVal
                    });
                    break;
                case 'splice':
                    formattedChanges.push({
                        type: 'splice',
                        path: path,
                        index: change.index,
                        removed: change.removed,
                        removedCount: change.removed.length,
                        added: change.object.slice(change.index, change.index+change.addedCount),
                        addedCount: change.addedCount
                    });
                    break;
                case 'delete':
                    formattedChanges.push({
                        type: 'delete',
                        path: path,
                        oldValue: change.oldValue
                    });
                    break;
                default:
                    formattedChanges.push({
                        type: 'other',
                        path: path
                    });
            }
        });

        if (self.status === 'declared') {
            dispatchChanges(formattedChanges);
        } else {
            addToQueue(dispatchChanges, [formattedChanges]);
        }
    }

    function unobserveValue() {
        if (typeof value === 'object' && value !== null) {
            Nested.unobserve(value, onValueChange);
        }
    }

    // Handle assignment that overwrites the whole value
    socket.on('replicantAssigned',  function (data) {
        if (data.name !== name || data.bundle !== bundle) return;
        log.replicants('replicantAssigned', data);

        if (data.originatorId === self.id) {
            self.emit('assignmentAccepted', data);
        }

        assignValue(data.newValue, data.revision);
    });

    // Handle changes to individual properties of the value, if it is an object or array
    // If we're not at the correct revision, request a full update
    socket.on('replicantChanged', function (data) {
        if (data.name !== name || data.bundle !== bundle) return;
        else if (data.revision !== (self.revision + 1)) {
            log.replicants('[%s] Replicant "%s" not at head revision (ours %s, theirs %s), fetching latest...',
                bundle, name, self.revision, data.revision);
            fullUpdate();
            return;
        }

        log.replicants('replicantChanged', data);

        var oldValue = clone(value);
        var replayChanges = clone(data.changes);
        replayChanges.reverse();
        replayChanges.forEach(function(change) {
            switch(change.type) {
                case 'add':
                    objectPath.del(oldValue, change.path);
                    break;
                case 'update':
                    objectPath.set(oldValue, change.path, change.oldValue);
                    break;
                case 'splice':
                    var arr = objectPath.get(oldValue, change.path);
                    if (!arr) arr = [];
                    var args = clone(change.removed);
                    args.unshift(change.addedCount);
                    args.unshift(change.index);
                    Array.prototype.splice.apply(arr, args);
                    objectPath.set(oldValue, change.path, arr);
                    break;
                case 'delete':
                    objectPath.set(oldValue, change.path, change.oldValue);
                    break;
                default:
                    objectPath.set(oldValue, change.path, change.oldValue);
            }
        });

        if (data.originatorId !== self.id) {
            unobserveValue();
            data.changes.forEach(function(change) {
                switch(change.type) {
                    case 'add':
                    case 'update':
                        objectPath.set(value, change.path, change.newValue);
                        break;
                    case 'splice':
                        var arr = objectPath.get(value, change.path);
                        var args = clone(change.added);
                        args.unshift(change.removedCount);
                        args.unshift(change.index);
                        Array.prototype.splice.apply(arr, args);
                        objectPath.set(value, change.path, arr);
                        break;
                    case 'delete':
                        objectPath.del(value, change.path);
                        break;
                }
            });
            observeValue();
        }

        self.revision = data.revision;
        self.emit('change', oldValue, value, data.changes);
    });

    function fullUpdate() {
        window.NodeCG.readReplicant(name, bundle, function(data) {
            self.emit('fullUpdate', data);
            assignValue(data.value, data.revision);
        });
    }

    // If we lose connection, redeclare everything on reconnect
    socket.on('disconnect', function() {
        self.status = 'undeclared';
    });
    socket.on('reconnect', function() {
        doBrowserDeclare(value);
    });
}
